import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import ScrollAnimation from 'react-animate-on-scroll';
import "animate.css/animate.min.css";


const OuterCon = styled.div`
    display: flex;
    flex-direction: column;

    width: 100vw;
    height: 100vh;
    background-color: pink;
`
const PhotoReel = styled.div`
    display: flex;
    flex-wrap: wrap;
    overflow: hidden;
    background-color: red;
    margin: 6% 5% 2% 5%;
`
//    overflow-x: scroll;

const ImageBox = styled.div`
    display: flex;
    width: 200px;
    height: 200px;
    margin: 5px;
`

const Picture = styled.img`
    object-fit: scale-down;
    width: 200px;
    height: 200px;
`

var images = []

function Work() {

    function importAll(r) {
        // let i = {};
        // r.keys().map((item, index) => { i[item.replace('./', '')] = r(item); });
        // return i;  
        let i = []
        r.keys().map((item) =>
            i.push({'image': require("../Images/MainScroll/"+item.replace('./', '')).default})
        );
        //console.log("hello world")
        //console.log(i)
        return i
    }

    images = importAll(require.context('../Images/MainScroll', false, /\.(png|jpe?g|svg)$/))
    // console.log(images)
    return(
        <OuterCon>
            <PhotoReel>
                {images.map((i, k) =>
                    <ImageBox>
                        <Picture src = {i.image}/>
                    </ImageBox>
                )}
            </PhotoReel>
        </OuterCon>
    );
}

export default Work
