import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import ScrollAnimation from 'react-animate-on-scroll';
import "animate.css/animate.min.css";
import { getImages } from '../api';

const OuterCon = styled.div`
    display: flex;
    flex-direction: column;

    width: 100vw;
    height: 100vh;
    background-color: pink;
`
const PhotoReel = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: flex-start;
    overflow: hidden;
    background-color: red;
    height: 92%;
    width: 90%;
    margin: 6% 5% 2% 5%;
    `
//    overflow-x: scroll;

const ImageBox = styled.div`
    display: flex;
    width: 25%;
    height: 50%;
    overflow: hidden;
`

const Picture = styled.img`
    object-fit: contain;
    height: 96%;
    width: 96%;
    padding: 2%;
`

function Work() {

    const [images, setImages] = useState([])
    const [prevImages, setPrevImages] = useState([])
    const [nextCursor, setNextCursor] = useState(null);
    const [prevCursor, setPrevCursor] = useState(null);

    useEffect(() => {
		const fetchData = async () => {
			const responseJson = await getImages(8, null);
			setImages(responseJson.resources);
			setNextCursor(responseJson.next_cursor);
		};
		fetchData();
        console.log(images)
	}, []);

    const handleLoadMoreButtonClick = async () => {
		const responseJson = await getImages(8, nextCursor);
        setPrevImages((currentPrev) => [
            ...currentPrev, 
            ... [...images]
        ]);
        console.log(prevImages)
		setImages((currentImageList) => [
			...responseJson.resources,
		]);
		setNextCursor(responseJson.next_cursor);
	};

    return(
        <OuterCon>
            <PhotoReel>
                {images.map((i, k) =>
                    <ImageBox>
                        <Picture src = {i.url}/>
                    </ImageBox>
                )}
            </PhotoReel>
            {nextCursor && (
                <button onClick={handleLoadMoreButtonClick}>
                    Next Page
                </button>
            )}
        </OuterCon>
    );
}

export default Work
