import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import ScrollAnimation from 'react-animate-on-scroll';
import "animate.css/animate.min.css";


const OuterCon = styled.div`
    display: flex;
    flex-direction: column;

    width: 100vw;
    height: 100vh;
    background-color: pink;
`
const PhotoReel = styled.div`
    display: flex;
    overflow-x: scroll;
    flex-direction: column;
    width: 70%;
    height: 100%;
    background-color: red;
    margin: 6% 15% 2% 15%;
`
const ReelBox = styled.div`
    display: flex;
`

const Picture = styled.img`
    object-fit: contain;
    width: 95%;
    height: 99%;
    margin: .5% 2.5% .5% 2.5%;
`

var images = []

function Work() {

    function importAll(r) {
        let i = []
        r.keys().map((item) =>
            i.push({'image': require("../Images/MainScroll/"+item.replace('./', '')).default})
        );
        return i
    }

    images = importAll(require.context('../Images/MainScroll', false, /\.(png|jpe?g|svg)$/))

    return(
        <OuterCon>
            <PhotoReel>
                <ReelBox>
                    {images.map((i, k) =>
                        <Picture src = {i.image}/>
                    )}
                </ReelBox>
            </PhotoReel>
        </OuterCon>
    );
}

export default Work
