import React, {useState, useEffect} from 'react';
import ImageGallery from 'react-image-gallery';
import styled, { keyframes } from 'styled-components'

const slide = keyframes`
    0% { 
        transform: translateX(100%);
    }
    100% {
        transform: translateX(0%);
    }
`

const Photo = styled.img`
    object-fit: cover;
    width: 100%;
    height: 100%;
    margin-right: 3px;
    transform: translateX(100%);
    animation: ${slide} 1s ease-in-out;
    animation-fill-mode: forwards;
`

// width: 90%;
// height: 80%;
// margin: 7% 5% 10% 5%;

const PhotoReel = styled.div`
    display: flex;
    flex-direction: row;
    width: 90%;
    height: 80%;
    margin: 7% 5% 10% 5%;
    overflow: hidden;
`

const PhotoHolder = styled.div`
    display: flex;
    flex-direction: row;
    width: 100vw;
    height: 100vh;
`

const images = [
    {
        picture: require('../Images/1.jpg'),
    },
    {
        picture: require('../Images/2.jpg'),
    },
    {
        picture: require('../Images/3.jpg'),
    },
    {
        picture: require('../Images/4.jpg'),
    },
    {
        picture: require('../Images/5.jpg'),
    },
    {
        picture: require('../Images/6.jpg'),
    },
]

const nullImage = [{picture: ''}]

function Image() {

    const [picIndex, setPicIndex] = useState(0)
    const numOfImages = images.length -1

    useEffect(() => {
        const interval = setInterval(() => {
            if(picIndex < numOfImages)
            {
                setPicIndex(picIndex+1)
                console.log("next photo")
            }
            else{
                setPicIndex(0)
                console.log("beginning photo")
            }
        }, 3000)
        return clearInterval(interval)
    }, [])

    return(
        <PhotoHolder>
            <PhotoReel>
                <Photo src = {images[picIndex].picture}/>
            </PhotoReel>
        </PhotoHolder>
    );
}

export default Image
