import styled from 'styled-components';
import ScrollAnimation from 'react-animate-on-scroll';
import "animate.css/animate.min.css";

const OuterCon = styled.div`
    display: flex;
    flex-direction: column;
    width: 90vw;
    height: 80vh;
    padding: 14vh 5vw 6vh 5vw;
    background-color: pink;
`
const InnerCon = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    background-color: red;
`

const ReviewDivider = styled.div`
    display: flex;
    flex-direction: column;
    align-content: center;
    align-items: center;
    height: 92%;
    width: 30%;
    padding: 4% 1% 4% 1%;
`

const ReviewImage = styled.img`
    object-fit: cover;
    width: 100%;
    height: 100%;
`

const ReviewContent = styled.div`

`

const ReviewBody = styled.p`

`

var images = []

function Reviews() {

    function importAll(r) {
        // let i = {};
        // r.keys().map((item, index) => { i[item.replace('./', '')] = r(item); });
        // return i;  
        let i = []
        r.keys().map((item) =>
            i.push(require("../Images/ReviewImages/" + item.replace('./', '')).default)
        )
        console.log(i)
        return i
    }

    images = importAll(require.context('../Images/ReviewImages', false, /\.(png|jpe?g|svg)$/))

    return(
        <OuterCon>
            <InnerCon>
                <ReviewDivider>
                    <ReviewImage src = {images[0]}/>
                    Review 1
                </ReviewDivider>
                <ReviewDivider>
                    Review 2
                    <ReviewImage src = {images[1]}/>
                </ReviewDivider>
                <ReviewDivider>
                    <ReviewImage src = {images[2]}/>
                    Review 3
                </ReviewDivider>
            </InnerCon>
        </OuterCon>
    );
}

export default Reviews
