import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import ScrollAnimation from 'react-animate-on-scroll';
import "animate.css/animate.min.css";
import { getImages } from '../api';

const OuterCon = styled.div`
    display: flex;
    flex-direction: row;
    width: 100vw;
    height: 100vh;
    background-color: pink;
`
const PhotoReel = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: flex-start;
    overflow: hidden;
    background-color: red;
    height: 92%;
    width: 90%;
    margin: 6% 1% 2% 1%;
    `
//    overflow-x: scroll;

const ImageBox = styled.div`
    display: flex;
    width: 25%;
    height: 50%;
    overflow: hidden;
`

const Picture = styled.img`
    object-fit: contain;
    height: 96%;
    width: 96%;
    padding: 2%;
`

function Work() {

    const [images, setImages] = useState([])
    const [prevImages, setPrevImages] = useState([])
    const [nextCursor, setNextCursor] = useState(null);
    const [nextCursorList, setNextCursorList] = useState(null);

    useEffect(() => {
		const fetchData = async () => {
			const responseJson = await getImages(8, null);
			setImages(responseJson.resources);
			setNextCursor(responseJson.next_cursor);
		};
		fetchData();
        setPrevImages([])
        //console.log(images)
	}, []);

    const handleLoadForwardButtonClick = async () => {
		const responseJson = await getImages(8, nextCursor);
        setPrevImages((currentPrev) => 
            [...currentPrev, images,]
        );
        setNextCursorList((current)=>
            [...current, nextCursor]
        );
		setImages((currentImageList) => [
			...responseJson.resources,
		]);
		setNextCursor(responseJson.next_cursor);
	};

    const handleLoadBackButtonClick = () =>
    {
        setImages(prevImages[prevImages.length - 1])
        setNextCursor(nextCursorList[nextCursorList.length - 1])
        setPrevImages((imageArrays) => imageArrays.filter((_, i) => i !== imageArrays.length - 1))
        setNextCursorList((cursors) => cursors.filter((_, i) => i !== cursors.length - 1))
	};

    return(
        <OuterCon>
            { prevImages.length > 0 && (
                <button onClick={handleLoadBackButtonClick}>
                    Prev Page 
                </button>
            )}
            <PhotoReel>
                {images.map((i, k) =>
                    <ImageBox>
                        <Picture src = {i.url}/>
                    </ImageBox>
                )}
            </PhotoReel>
            {nextCursor && (
                <button onClick={handleLoadForwardButtonClick}>
                    Next Page
                </button>
            )}
        </OuterCon>
    );
}

export default Work
