import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import ScrollAnimation from 'react-animate-on-scroll';
import "animate.css/animate.min.css";


const OuterCon = styled.div`
    display: flex;
    flex-direction: column;
    width: 100vw;
    height: 100vh;
    background-color: pink;
`
const InnerCon = styled.div`
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    width: 90%;
    height: 100%;
    background-color: red;
    margin: 8% 5% 2% 5%;
`
const FirstRow = styled.div``

const SecondRow = styled.div``

const Picture = styled.img`
    object-fit: cover;
    width: 100%;
    height: 100%;
`


var images = []

function Work() {

    function importAll(r) {
        // let i = {};
        // r.keys().map((item, index) => { i[item.replace('./', '')] = r(item); });
        // return i;  
        let i = []
        r.keys().map((item) =>
            i.push({'image' : require("../Images/MainScroll/"+item.replace('./', '')).default, 'caption' : "photo"})
        );
        //console.log("hello world")
        return i
    }

    images = importAll(require.context('../Images/MainScroll', false, /\.(png|jpe?g|svg)$/))
    // console.log(images)
    return(
        <OuterCon>
            <InnerCon>
                {images.map((image) => {
                    <Picture src = {image}
                    />
                })}
            </InnerCon>
        </OuterCon>
    );
}

export default Work
