import React, {useState, useEffect} from 'react';
import styled, { keyframes } from 'styled-components'
import useInterval from 'react-useinterval';

// const slide = keyframes`
//     0% { 
//         visibility: hidden;
//     }
//     20%, 100% {
//         visibility: visable;
//     }
// `

const fadeIn = keyframes`
  0% {
    opacity: .3;
  }

  100% {
    opacity: 1;
  }
`
const NoAnimation = keyframes`
`

const Photo = styled.img`
    object-fit: cover;
    width: 100%;
    height: 100%;
    animation: ${props => props.fade? fadeIn: NoAnimation} 1.5s linear;
    animation-delay: .1s;
    margin-right: 3px;
`

//transform: translateX(100%);

const PhotoReel = styled.div`
    display: flex;
    flex-direction: row;
    width: 90%;
    height: 80%;
    margin: 7.5% 5% 10% 5%;
    overflow: hidden;
`

const PhotoHolder = styled.div`
    display: flex;
    flex-direction: row;
    width: 100vw;
    height: 100vh;
`

const images = [
    {
        picture: require('../Images/1.jpg'),
    },
    {
        picture: require('../Images/3.jpg'),
    },
    {
        picture: require('../Images/4.jpg'),
    },
    {
        picture: require('../Images/5.jpg'),
    },
    {
        picture: require('../Images/6.jpg'),
    },
]

function Image() {

    const [picIndex, setPicIndex] = useState(0)
    const [fade, setFade] = useState(false)
    const [count, setCount] = useState(1)
    const numOfImages = images.length-1

    const ChangePic = () =>
    {
        // console.log(count)
        // console.log(picIndex)
        //divisable by 4 changes image every 4 seconds
        if(count % 5 === 0 )
        {
            if(picIndex === numOfImages)
            {
                setCount(0)
                setPicIndex(0)
            }
            else
            {
                setPicIndex(p => p + 1)
            }
            setCount(c => c + 1)
            setFade(true)
        }
        else
        {
            setCount(c => c + 1)
            setFade(false)
        }
    }

    useInterval(ChangePic, 1500)

    return(
        <PhotoHolder>
            <PhotoReel>
                <Photo fade = {fade} src = {images[picIndex].picture}/>
            </PhotoReel>
        </PhotoHolder>
    );
}

export default Image
