import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import ScrollAnimation from 'react-animate-on-scroll';
import "animate.css/animate.min.css";
import { getImages } from '../api';

const OuterCon = styled.div`
    display: flex;
    flex-direction: col;
    justify-content: center;
    align-items: center;
    height: 80vh;
    width: 96vw;
    background-color: pink;
    padding: 17vh 2vw 3vh 2vw;
`

const PhotoMenuBar = styled.div`
    display: flex;
    flex-direction: row;
    background-color: yellow;
    width: 100%;
    height: 3vh;
`
const PhotoComponent = styled.div`
    display: flex;
    flex-direction: row;
`

const PhotoReel = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: flex-start;
    overflow: hidden;
    background-color: red;
    height: 98%;
    width: 80%;
    margin: 0% 0% 0% 0%;
    padding: 1% 8% 1% 8%;
    `

const RightButton = styled.button`
    height: 5%;
    width: 5%;
    margin-left: -5%;
    z-index: 4;
`
const LeftButton = styled.button`
    height: 5%;
    width: 5%;
    margin-right: -5%;
    z-index: 4;
`

const ImageBox = styled.div`
    display: flex;
    width: 25%;
    height: 50%;
    overflow: hidden;
`

const Picture = styled.img`
    object-fit: contain;
    height: 96%;
    width: 96%;
    padding: 2%;
`

function Work() {

    const [images, setImages] = useState([])
    const [prevImages, setPrevImages] = useState([])
    const [nextCursor, setNextCursor] = useState(null);
    const [nextCursorList, setNextCursorList] = useState([]);

    useEffect(() => {
		const fetchData = async () => {
			const responseJson = await getImages(8, null);
			setImages(responseJson.resources);
			setNextCursor(responseJson.next_cursor);
		};
		fetchData();
        setPrevImages([])
        //console.log(images)
	}, []);

    const handleLoadForwardButtonClick = async () => {
		const responseJson = await getImages(8, nextCursor);
        setPrevImages((currentPrev) => 
            [...currentPrev, images,]
        );
        setNextCursorList((current)=>
            [...current, nextCursor]
        );
        //console.log(nextCursorList)
		setImages((currentImageList) => [
			...responseJson.resources,
		]);
		setNextCursor(responseJson.next_cursor);
	};

    const handleLoadBackButtonClick = () =>
    {
        setImages(prevImages[prevImages.length - 1])
        //console.log("Back Delete from")
        //console.log(nextCursorList)
        setNextCursor(nextCursorList[nextCursorList.length - 1])
        setPrevImages((imageArrays) => imageArrays.filter((_, i) => i !== imageArrays.length - 1))
        setNextCursorList((cursors) => cursors.filter((_, i) => i !== cursors.length - 1))
	};

    return(
        <OuterCon>
            <PhotoMenuBar>

            </PhotoMenuBar>
            <PhotoComponent>
                { prevImages.length > 0 && (
                    <LeftButton onClick={handleLoadBackButtonClick}>
                        Prev Page 
                    </LeftButton>
                )}
                <PhotoReel>
                    {images.map((i, k) =>
                        <ImageBox>
                            <Picture src = {i.url}/>
                        </ImageBox>
                    )}
                </PhotoReel>
                {nextCursor && (
                    <RightButton onClick={handleLoadForwardButtonClick}>
                        Next Page
                    </RightButton>
                )}
            </PhotoComponent>
        </OuterCon>
    );
}

export default Work
