import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import ScrollAnimation from 'react-animate-on-scroll';
import "../../node_modules/animate.css/animate.min.css";


const OuterCon = styled.div`
    display: flex;
    flex-direction: row;
    width: 100vw;
    height: 100vh;
`
const InnerCon = styled.div`
    display: flex;
    flex-direction: column;
    width: 80%;
    height: 70%;
    background-color: red;
    margin: 7% 8% 7% .5%;
`
const FirstRow = styled.div`
    display: flex;
    flex-direction: row;
    margin: 5% 0% 0% 5%;
`

const SecondRow = styled.div`
    margin: -2% 5% 5% 5%;
`

const AboutBody = styled.p`
`
const AboutMeDiv = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-content: center;
    align-items: center;
    width: 60%;
    height: 70%;
    background-color: red;
    margin: 7% .5% 7% 8%;`

const AboutMeImage = styled.img`
    object-fit: cover;
    width: 100%;
    height: 100%;
    border-radius: 50%;
`
const DividerBar = styled.hr`
    border: 10px solid black;
    border-radius: 5px;
    margin-top: 5%;
`

function About() {

    return(
        <OuterCon>
            <AboutMeDiv>
                <ScrollAnimation animateIn="animate__animated animate__fadeIn">
                <AboutMeImage src = {require('../Images/Selfy.jpg')}/>
                </ScrollAnimation>
            </AboutMeDiv>
            <InnerCon>
                <FirstRow>
                    <ScrollAnimation animateIn="animate__animated animate__fadeInDown">
                        <h1>
                            Arlette Nguyen
                        </h1>
                    </ScrollAnimation>
                </FirstRow>
                <SecondRow>
                    <AboutBody>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt 
                    ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco 
